import React from 'react';
import { Link } from 'react-router-dom';

// New Navigation Component with Logo
const Navigation = () => (
  <nav style={styles.nav}>
    <img src={logo} alt="Smart Finance Logo" style={styles.logo} />
    <Link to="/" style={styles.navLink}>Home</Link>
    <Link to="/form" style={styles.navLink}>Form</Link>
    <Link to="/products" style={styles.navLink}>Products</Link>
    <Link to="/cart" style={styles.navLink}>Cart</Link>
  </nav>
);

// New About Component with Enhanced Content
const About = () => (
  <section style={styles.details}>
    <h2>About Smart Finance Advisor</h2>
    <p>
      Smart Finance Advisor is an AI-driven financial advisory platform built using
      <strong> React, MySQL,</strong> and <strong>Rasa Pro</strong>. Our mission is to provide real-time financial insights,
      helping you achieve your financial goals.
    </p>
    <p>
      From tracking income and managing expenses to providing investment suggestions, our tool leverages
      AI to offer personalized recommendations tailored to your unique financial situation.
    </p>
  </section>
);

const Home = () => {
  return (
    <div style={styles.container}>
      <Navigation />
      <header style={styles.hero}>
        <img src={heroBanner} alt="Finance Banner" style={styles.heroImage} />
        <h1>Welcome to Smart Finance Advisor</h1>
        <p>Your AI-powered financial advisory tool to help you make smarter decisions with your money.</p>
        <Link to="/form">
          <button style={styles.button}>Get Started</button>
        </Link>
      </header>

      <About /> {/* About section below the hero */}
    </div>
  );
};

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    textAlign: 'center',
    backgroundColor: '#F9F9F9', // Light background for contrast
    minHeight: '100vh', // Full height to prevent gaps
  },
  nav: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#007BFF',
    padding: '15px 20px',
    boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)',
  },
  logo: {
    height: '40px',
    marginRight: '20px',
  },
  navLink: {
    color: 'white',
    textDecoration: 'none',
    margin: '0 15px',
    fontSize: '18px',
    fontWeight: '500',
    transition: 'color 0.3s, transform 0.3s',
  },
  navLinkHover: {
    color: '#FFD700',
    transform: 'scale(1.1)',
  },
  hero: {
    position: 'relative',
    color: 'white',
    padding: '100px 20px',
    marginBottom: '20px',
    borderRadius: '0 0 50px 50px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
  },
  heroImage: {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    objectFit: 'cover',
    zIndex: -1, // Ensures text and buttons are above image
    borderRadius: '0 0 50px 50px',
    opacity: 0.8, // Slight transparency for text visibility
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
    transition: 'background-color 0.3s',
  },
  details: {
    backgroundColor: 'white',
    color: 'black',
    padding: '50px 20px',
    marginTop: '20px',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    maxWidth: '800px',
    margin: 'auto',
    textAlign: 'left', // Align text to the left for readability
  },
};

export default Home;
