// src/components/Home.js
import React from 'react';
import { Link } from 'react-router-dom';

// New Navigation Component
const Navigation = () => (
  <nav style={styles.nav}>
    <Link to="/" style={styles.navLink}>Home</Link>
    <Link to="/form" style={styles.navLink}>Form</Link>
    <Link to="/products" style={styles.navLink}>Products</Link>
    <Link to="/cart" style={styles.navLink}>Cart</Link>
  </nav>
);

// New About Component
const About = () => (
  <section style={styles.details}>
    <h2>About the Project</h2>
    <p>
      This project is built using React, MySQL, and Rasa Pro to provide real-time financial advice.
      You can track your income, manage your expenses, and get recommendations for investments and savings.
    </p>
    <p>
      Our AI-powered tool analyzes your financial data and gives you personalized recommendations to help
      you achieve financial stability.
    </p>
  </section>
);

const Home = () => {
  return (
    <div style={styles.container}>
      <Navigation />
      <header style={styles.hero}>
        <h1>Welcome to Smart Finance Advisor</h1>
        <p>Your AI-powered financial advisory tool to help you make smarter decisions with your money.</p>
        <Link to="/form">
          <button style={styles.button}>Get Started</button>
        </Link>
      </header>

      <About />  {/* About section below the hero */}
    </div>
  );
};

const styles = {
  container: {
    fontFamily: 'Arial, sans-serif',
    textAlign: 'center',
    backgroundColor: 'white', // Set background color to white
    minHeight: '00vh', // Ensure the container takes full height
  },
  nav: {
    display: 'flex',
    justifyContent: 'center',
    backgroundColor: '#007BFF',
    padding: '15px 0', // Increased padding for a better look
    boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)', // Added shadow for depth
  },
  navLink: {
    color: 'white',
    textDecoration: 'none',
    margin: '0 20px', // Increased margin for spacing
    fontSize: '18px', // Increased font size
    fontWeight: '500', // Slightly bolder text
    transition: 'color 0.3s, transform 0.3s', // Smooth transition for hover effects
  },
  navLinkHover: {
    color: '#FFD700', // Gold color on hover
    transform: 'scale(1.1)', // Slightly enlarge on hover
  },
  hero: {
    backgroundColor: 'white',
    color: 'black',
    padding: '100px 20px',
    marginBottom: '20px',
    borderRadius: '0 0 50px 50px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '60vh', // Full height for the hero section
  },
  button: {
    padding: '10px 20px',
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
    transition: 'background-color 0.3s',
  },
  details: {
    backgroundColor: 'white',
    color: 'black',
    padding: '100px 20px',
    marginBottom: '20px',
    borderRadius: '0 0 50px 50px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    height: '60vh',
  },
};

export default Home;
